import React from "react";
import { useParams } from "react-router-dom";
import bookingData from "../data.json";
import { Button } from "antd";
import dayjs from "dayjs";
import { getBookingsForNextWeek, getBookingsForWeek } from "../util";
const Index = () => {
  const { roomId } = useParams();
  let now = "2019-09-22";

  let dataBooks = getBookingsForWeek({ now, roomId, weekNo: 1 });
  return (
    <div style={{ display: "flex", width: "100%", height: "100vh" }}>
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          backgroundColor: "#46529D",
        }}
      >
        <div
          style={{
            width: "100%",
            height: "135px",
            backgroundColor: "#2EBAEE",
          }}
        >
          <p>{roomId}</p>
        </div>

        <p>Upcoming</p>
        <p>{dayjs(now).format("dddd")}</p>
        <p>{dayjs(now).format("DD MMM")}</p>
        {dataBooks?.map((item, index) => {
          return (
            <div key={index}>
              <p>
                {dayjs(item.startTime).format("hh:mm")}
                {" - "}
                {dayjs(item.endTime).format("hh:mm")}
              </p>
              <p>{item.title}</p>
            </div>
          );
        })}
      </div>
      <div
        style={{
          display: "flex",
        }}
      ></div>
    </div>
  );
};

export default Index;
