import React from "react";
import { useParams } from "react-router-dom";
import bookingData from "../data.json";
import { Tabs } from "antd";
import dayjs from "dayjs";
import { getBookingsForNextWeek, getBookingsForWeek } from "../util";

const Index = () => {
  const { roomId } = useParams();
  let now = "2019-09-23";

  let dataBooks = getBookingsForWeek({ now, roomId, weekNo: 1 });
  console.log(new Date(dataBooks));

  const styleTime = {
    fontWeight: 400,
    fontSize: "16px",
    lineHeight: "100%",
    color: "#ffffff",
    opacity: "0.5",
  };

  const styleTitle = {
    fontWeight: 400,
    fontSize: "20px",
    lineHeight: "100%",
    color: "#ffffff",
  };

  return (
    <div style={{ display: "flex", width: "100%", height: "100vh" }}>
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          backgroundColor: "#46529D",
          paddingLeft: "90px",
        }}
      >
        <div
          style={{
            width: "495px",
            height: "135px",
            backgroundColor: "#2EBAEE",
            marginBottom: "125px",
          }}
        >
          <div style={{ margin: "56px 0 25px 48px" }}>
            <span
              style={{ fontWeight: 700, fontSize: "54px", color: "#ffffff" }}
            >
              {roomId}
            </span>
          </div>
        </div>
        <span
          style={{
            fontFamily: "Lato",
            fontWeight: 700,
            fontSize: "18px",
            color: "#ffffff",
            marginBottom: "58px",
          }}
        >
          Upcoming
        </span>
        <span
          style={{
            fontWeight: 300,
            fontSize: "64px",
            lineHeight: "100%",
            color: "#ffffff",
            opacity: "0.5",
            marginBottom: "15px",
          }}
        >
          {dayjs(now).format("dddd")}
        </span>
        <span
          style={{
            fontWeight: 300,
            fontSize: "64px",
            lineHeight: "100%",
            color: "#ffffff",
            marginBottom: "68px",
          }}
        >
          {dayjs(now).format("DD MMM")}
        </span>
        {dataBooks
          .filter(
            (d) =>
              new Date(dataBooks[0].startTime).toDateString() ===
              new Date(d.startTime).toDateString()
          )
          ?.map((item, index) => {
            return (
              <div key={index} style={{ marginBottom: "16px" }}>
                <span style={styleTime}>
                  {dayjs(item.startTime).format("hh:mm")}
                  {" - "}
                  {dayjs(item.endTime).format("hh:mm")}
                </span>
                <div style={{ marginBottom: "8px" }} />
                <span style={styleTitle}>{item.title}</span>
              </div>
            );
          })}
      </div>

      <div
        style={{
          display: "flex",
        }}
      ></div>
    </div>
  );
};

export default Index;
