import React from "react";
import { useParams } from "react-router-dom";
import bookingData from "../data.json";
import { Button } from "antd";
import dayjs from "dayjs";
import { getBookingsForNextWeek, getBookingsForWeek } from "../util";
const Index = () => {
  const { roomId } = useParams();
  let now = "2019-09-22";
  let mock = bookingData[0];

  let data = getBookingsForWeek({ now, roomId, weekNo: 1 });
  console.log(data);
  return (
    <div style={{ display: "flex", width: "100%", height: "100vh" }}>
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          backgroundColor: "#46529D",
        }}
      >
        <div
          style={{
            width: "100%",
            height: "135px",
            backgroundColor: "#2EBAEE",
          }}
        >
          <p>{roomId}</p>
        </div>

        <p>Upcoming</p>
        <p>{dayjs(now).format("dddd")}</p>
        <p>{dayjs(now).format("DD MMM")}</p>
        <p>{mock.title}</p>
      </div>
      <div
        style={{
          display: "flex",
        }}
      ></div>
    </div>
  );
};

export default Index;
